    .Does(() =>
    {
        SetSqlCommandTimeout(60);
        using (var connection = OpenSqlConnection(@"Data Source=(localdb)\MSSqlLocalDb;Initial Catalog=MyDatabase"))
        {
            ExecuteSqlCommand(connection, "...");
            ExecuteSqlFile(connection, "./somePath/MyScript.sql");
        }
    });
